StateMachineElement.EntryScript:
Square.alpha = 255;
colWidth = Grid.width/Grid.columns;
rowHeight = Grid.height/Grid.rows;
Square.x = operatorClick.x-(operatorClick.x%colWidth)+colWidth/2;
Square.y = operatorClick.y-(operatorClick.y%rowHeight)+rowHeight/2;
Square.width = SquareDimension.value;
Square.height = SquareDimension.value;
Square.subjectView = true;
Target.x = Square.x;
Target.y = Square.y;

LeftTarget.radius = PeripheralTargetRadius.value;
RightTarget.radius = PeripheralTargetRadius.value;
FixationGraphic.visible = false;
EventInfo.setData("Event",8);
EventInfo.updateValue();

EventInfo.setData("Stage",6);
EventInfo.setData("Event",3);
EventInfo.updateValue();
RightGraphic.visible = false;
RewardR1.visible = false;
RewardR2.visible = false;
RewardR3.visible = false;
RewardR4.visible = false;
RewardR5.visible = false;
RewardR6.visible = false;
RewardR7.visible = false;
RewardR8.visible = false;

EventInfo.setData("Stage",6);
EventInfo.setData("Event",3);
EventInfo.updateValue();
LeftGraphic.visible = false;
RewardL1.visible = false;
RewardL2.visible = false;
RewardL3.visible = false;
RewardL4.visible = false;
RewardL5.visible = false;
RewardL6.visible = false;
RewardL7.visible = false;
RewardL8.visible = false;

StateMachineElement.Script:
EventInfo.setData("Stage",6);
EventInfo.setData("Event",3);
EventInfo.updateValue();
TrialInfo.setData("ChosenSide",0);
if(SuperblockType.value == 0)
	TrialInfo.setData("ChosenCol",1+Number(LeftIsRed.value == true));
else
	TrialInfo.setData("ChosenCol",3+Number(LeftIsRed.value == true));
return true;

EventInfo.setData("Stage",6);
EventInfo.setData("Event",3);
EventInfo.updateValue();
TrialInfo.setData("ChosenSide",1);
if(SuperblockType.value == 0)
	TrialInfo.setData("ChosenCol",1+Number(LeftIsRed.value == false));
else
	TrialInfo.setData("ChosenCol",3+Number(LeftIsRed.value == false));
return true;

if(LeftGraphic.visible)
	return LeftIsRed.value == true;
else
	return LeftIsRed.value == false;

if(RightGraphic.visible) 
	FeedbackRing.x = 525;
else
	FeedbackRing.x = 275; return true;

if(SuperblockType.value == 0) 
{
	if(RightGraphic.visible)
	{
		FeedbackRing.setColor( RightGraphic.red, RightGraphic.green, RightGraphic.blue);
		TrialInfo.setData("FeedbackCol",Number(LeftIsRed.value == false));
	}
	else
	{
		FeedbackRing.setColor( LeftGraphic.red, LeftGraphic.green, LeftGraphic.blue); return true;           
		TrialInfo.setData("FeedbackCol",Number(LeftIsRed.value));
	}
}
else
{
	//In Diamond/Square - Orange/Cyan trials, green/red is random
	if(GreyFeedback.value)
		FeedbackRing.setColor(0,255,0);
	else
		FeedbackRing.setColor(255, 0, 0);
}

RewardList = [RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
if(RightGraphic.visible)
	RewardList = [RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
while(RewardsChecked.value<8)
{
	RewardSelector.randomize();		
	RewardsChecked.value++;
	if(RewardList[RewardSelector.value].visible)
	{
		CurrentReward.value = RewardSelector.value;
		return true;
	}
}
return false;

StateMachineElement.FrameScript:
RewardList = [RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
if(RightGraphic.visible)
	RewardList = [RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
if(RewardList[CurrentReward.value].radius > 1)
	RewardList[CurrentReward.value].radius--;
else
	RewardList[CurrentReward.value].visible = false;

StateMachineElement.Script:
if(RightGraphic.visible) 
	if(LeftIsRed.value)
		if(SuperblockType.value)
			EventInfo.setData("Event",14);
		else
			EventInfo.setData("Event",10);
	else
		if(SuperblockType.value)
			EventInfo.setData("Event",16);
		else
			EventInfo.setData("Event",12);
else 
	if(LeftIsRed.value == false)
		if(SuperblockType.value)
			EventInfo.setData("Event",14);
		else
			EventInfo.setData("Event",10);
	else
		if(SuperblockType.value)
			EventInfo.setData("Event",16);
		else
			EventInfo.setData("Event",12);
EventInfo.updateValue();

if(RightGraphic.visible) 
	if(RightGraphic.green != 0)
		EventInfo.setData("Event",11);
	else
		EventInfo.setData("Event",13);
else 
	if(LeftGraphic.green != 0)
		EventInfo.setData("Event",11);
	else
		EventInfo.setData("Event",13);
EventInfo.updateValue();

StateMachineElement.EntryScript:
EventInfo.setData("Event",9);
EventInfo.updateValue();

if(TrialType.value<=10)
{
	LeftIsRed.value = true;
	if(SuperblockType.value == 0)
	{
		LeftGraphic.setColor(255,0,0);
		RightGraphic.setColor(0,255,0);
	}
	else
	{
		if(BlockType.value == 1)
		{
			LeftGraphic.shape = "Rectangle";
			RightGraphic.shape = "Diamond";
			LeftGraphic.setDimensions(35,35);
			RightGraphic.setDimensions(49,49);
		}
		else
		{
			LeftGraphic.setColor(255,165,0);
			RightGraphic.setColor(0,255,255);
		}
	}
}
else
{
	LeftIsRed.value = false;
	if(SuperblockType.value == 0)
	{
		LeftGraphic.setColor(0,255,0);
		RightGraphic.setColor(255,0,0);
	}
	else
	{
		if(BlockType.value == 1)
		{
			RightGraphic.shape = "Rectangle";
			LeftGraphic.shape = "Diamond";
			LeftGraphic.setDimensions(49,49);
			RightGraphic.setDimensions(35,35);
		}
		else
		{
			RightGraphic.setColor(255,165,0);
			LeftGraphic.setColor(0,255,255);
		}
	}
}
LeftGraphic.visible = true;
RightGraphic.visible = true;

EventInfo.setData("Event",9);
EventInfo.updateValue();
LRewards =[RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
RRewards =[RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
GreenRewardList = RRewards;
RedRewardList = LRewards;
if(LeftIsRed.value == false)
{
	GreenRewardList = LRewards;
	RedRewardList = RRewards;
}
for(i=0;i<GreenRewards.value;i++)
{
	RewardSelector.randomize();
	GreenRewardList[RewardSelector.value].visible = true;
}
RewardSelector.reset();
for(i=0;i<RedRewards.value;i++)
{
	RewardSelector.randomize();
	RedRewardList[RewardSelector.value].visible = true;
}

//Calculate Variable Delay and start time
randDouble.randomize();
trunc_exp = (1-Math.pow(Math.E,-2.5))*(randDouble.value);
trialFixationTargetOverlapTime = 500 - Math.log(1-trunc_exp)*250;   
Delay.value = trialFixationTargetOverlapTime;
Timer.restart();

StateMachineElement.Script:
	//Select Reward amounts
	switch(TrialType.value%10)
	{
	case 0:
		GreenRewards.value = 8;
		RedRewards.value = 1;
		break;
	case 1:
	  	GreenRewards.value = 4;
	  	RedRewards.value = 1;
	  	break;
	case 2:
	  	GreenRewards.value = 2;
	  	RedRewards.value = 1;
	  	break;
	case 3:
	  	GreenRewards.value = 1;
	  	RedRewards.value = 1;
	  	break;
	case 4:
	  	GreenRewards.value = 4;
	  	RedRewards.value = 2;
	  	break;
	case 5:
	  	GreenRewards.value = 1;
	 	 RedRewards.value = 2;
	  	break;
	case 6:
	  	GreenRewards.value = 4;
	  	RedRewards.value = 4;
	  	break;
	case 7:
	  	GreenRewards.value = 2;
	  	RedRewards.value = 4;
	  	break;
	case 8:
	  	GreenRewards.value = 1;
	  	RedRewards.value = 4;
	  	break;
	case 9:
	  	GreenRewards.value = 1;
	  	RedRewards.value = 8;
	  	break;
	}
TrialInfo.setData("GreenRew",GreenRewards.value);
TrialInfo.setData("RedRew",RedRewards.value);

//Choose Reward Feedback Color
GreyFeedback.randomize();
TrialInfo.setData("FeedbackCol",Number(!GreyFeedback.value));

return true;

StateMachineElement.EntryScript:
EventInfo.setData("Stage",0);
EventInfo.setData("Event",1);
EventInfo.updateValue();

//Reset TrialInfo
TrialInfo.setData("CorrectTrial",0);
TrialInfo.setData("Rewarded",0);
TrialInfo.setData("Override",0);
if(BlockType.value || SuperblockType.value)
{
	TrialInfo.setData("GreenProb",LowProbability.value);
	TrialInfo.setData("RedProb",HighProbability.value);
}
else
{
	TrialInfo.setData("RedProb",LowProbability.value);
	TrialInfo.setData("GreenProb",HighProbability.value);
}

TrialInfo.setData("GreenRew",-1);
TrialInfo.setData("RedRew",-1);
TrialInfo.setData("FeedbackCol",-1);
TrialInfo.setData("ChosenCol",-1);
TrialInfo.setData("ChosenSide",-1);

StateMachineElement.ExitScript:
EventInfo.setData("Event",2);
EventInfo.updateValue();
EventInfo.setData("Trial",EventInfo.getDataAsInt("Trial")+1);
AttemptedTrials.value++;
BlockPerformance.setData("%Correct Curr Block",(100*CorrectTrials.value/AttemptedTrials.value).toFixed(2));
BlockPerformance.updateValue();
TrialInfo.updateValue();

EventInfo.setData("Event",4);
EventInfo.updateValue();

//Mark Correct Trial
TrialInfo.setData("CorrectTrial",1);
CorrectTrials.value++;

StateMachineElement.EntryScript:
//This data wasn't really meaningful
TrialInfo.setData("Block",BlockType.value);
TrialInfo.setData("TrialInBlock",0);
if(SuperblockType.value == 0)
	TrialInfo.setData("Volatility",2);
else
	TrialInfo.setData("Volatility",BlockType.value);
BlockPerformance.setData("%Correct Curr Block",0);
BlockPerformance.updateValue();

StateMachineElement.Script:
switch(SuperblockMode.value) 
{
case 0:
	BlockLength.value = 20;
	return ++BlocksSoFar.value < 4
case 1:
	BlockLength.value = 20;
	if(BlocksSoFar.value == 0)
		BlockLength.value = 40;
	return ++BlocksSoFar.value < 3
case 2:
	BlockLength.value = 40;
	return ++BlocksSoFar.value < 2
}
return false;

