StateMachine.EntryScript:
expInfo.setData("Trial",-1);
expInfo.setData("Block",-1);
expInfo.setData("TrialInBlock",-1);
expInfo.setData("GoodTrials",0);
expInfo.setData("GoodTrials(%)",0);
expInfo.updateValue();

StateMachineElement.EntryScript:
expInfo.setData("Block",blockNum.value);
expInfo.setData("TrialInBlock",-1);
expInfo.updateValue();
//Set targetSelctor range
targetSelector.max = numTargets.value-1;

//Initialize targetCounter array with zeroes for each target
for(var i=0;i<numTargets.value;i++)
{
targetCounter.setData(i,0);
}

expInfo.setData("Trial",expInfo.getDataAsInt("Trial")+1);
expInfo.setData("TrialInBlock",expInfo.getDataAsInt("TrialInBlock")+1);
expInfo.updateValue();
tokenTray.width = 2*mmToPixel(mmTrayRad.value);
tokenTray.height = tokenTray.width;
tokenTray.numTokens = numTargets.value;
tokenTray.tokenWidth = 2*mmToPixel(mmCueRad.value);
tokenTray.tokenHeight =tokenTray.tokenWidth;
tokenTray.phase = targetStartPos.value*45.0;
fixGraphic.radius = mmToPixel(mmCueRad.value);
fixGraphic.setColor(cueR.value,cueG.value,cueB.value,255);
fixTarget.radius = mmToPixel(mmFixWinRad.value);
sacTarget.radius = mmToPixel(mmSacWinRad.value);
sacTarget.x = tokenTray.getTokenX(targetSelector.value);
sacTarget.y = tokenTray.getTokenY(targetSelector.value);
Reward.number = numRewards.value;

Result.EntryScript:
//Trial was not completed succefully, reshuffle this target value.
targetSelector.reshuffleLastValue();
expInfo.setData("GoodTrials(%)",Math.round(100*expInfo.getDataAsDouble("GoodTrials")/(expInfo.getDataAsDouble("Trial")+1)));
expInfo.updateValue();

StateMachineElement.FrameScript:
if(fixationController.userEnteredTarget()){
fixationAcquired.value = true;
}
calibratePos();
//We turn this on after the first frame
//because Min's display has a known 20ms
//input lag.  This puts the signal within 
//about 3ms of when the monkey actually 
//sees the fixation point.
fixOn.value = true;

StateMachineElement.EntryScript:
tokenTray.visible= true;
tokenTray.setTokenColor(targetSelector.value,cueR.value,cueG.value,cueB.value,255);
cueController.fixationTime = cueMs.value;
cueController.totalTime = cueController.fixationTime+100;

StateMachineElement.FrameScript:
//Turn off CueIDEvent after one frame.
frameCtr.value++;
if(frameCtr.value > 1)
	eventCode.value = 0x80;
else
	eventCode.value = 0x7F & targetSelector.value;

calibratePos();
cueOn.value = true;

StateMachineElement.EntryScript:
DelayController.fixationTime = delayMs.value;
DelayController.totalTime = DelayController.fixationTime+100;

//Choose distractor
if(useDistractor.value)
{
	var numTargs = tokenTray.numTokens;
	var distOps = new Array();
	var targInd = targetSelector.value;
	//Append adjacent values to list
	if(enAdjacentDistractor.value)
	{
		distOps.push((targInd+numTargs+1)%numTargs);
		distOps.push((targInd+numTargs-1)%numTargs);
	}
	//Append double adjacent values to list
	if(enDoubleAdjDistractor.value)
	{
		distOps.push((targInd+numTargs+2)%numTargs);
		distOps.push((targInd+numTargs-2)%numTargs);
	}
	//Append remaining values to list
	if(numTargs > 5)
	{
		var boundInd1 = (targInd+numTargs+3)%numTargs;
		var boundInd2 = (targInd+numTargs-2)%numTargs;
		for(var i=boundInd1;i!=boundInd2;i=(i+1)%numTargs)
		{
			distOps.push(i);
		}
	}
	//Select value
	var selectedArrayInd = Math.floor(Math.random()*distOps.length);
	//Set distractor index to variable
	distractorIndex.value = distOps[selectedArrayInd];
}

StateMachineElement.ExitScript:
fixGraphic.visible = false;
fixOn.value = false;

//In case the distractor didn't get turned off, turn it off. 
tokenTray.setTokenColor(distractorIndex.value,0,0,0,255);

StateMachineElement.FrameScript:
calibratePos();


if(useDistractor.value)
{
	if(!distractorShown.value)
	{
		if(distractorTimer.value >= distractorOnset.value)
		{
			tokenTray.setTokenColor(distractorIndex.value,distractorColor.value);
			distractorShown.value = true;
		}
	}
	else
	{
		if(distractorTimer.value >= (distractorOnset.value + distractorInterval.value))
		{
			tokenTray.setTokenColor(distractorIndex.value,0,0,0,255);
		}
	}
}

StateMachineElement.EntryScript:
if(useReminderCue.value)
{
	tokenTray.visible= true;
     tokenTray.setTokenColor(targetSelector.value,cueR.value,cueG.value,cueB.value,255);
}

MemoryController.fixationTime = memHoldMs.value;
MemoryController.maxAcquisitionTime = memAcqMs.value;
MemoryController.totalTime = MemoryController.fixationTime+MemoryController.maxAcquisitionTime;

ScriptFunction.Script:
if(!(operatorClick.newClick && !(operatorClick.newClick = false)))
	return;
var x = posSig.getValue("x"); 
var y = posSig.getValue("y");
var text = "";
if((Experiment.xGain == 0) || (Experiment.yGain == 0))
{
	text =		"Calibration does not work\n"+
				"when either gain value is\n"+
				"zero"
}
else
{
	Experiment.xOffset = Experiment.xOffset + (fixGraphic.x-x)/Experiment.xGain;
	Experiment.yOffset = Experiment.yOffset + (fixGraphic.y-y)/Experiment.yGain;
	text = "Offsets were calculated"
}
calibFeedback.text = text;
calibFeedback.setPos(fixGraphic.x+2*fixGraphic.radius,fixGraphic.y-fixGraphic.radius);
calibFeedback.visible = true;

StateMachineElement.EntryScript:
//In Arnsten Pictobox, the 8th bit
//is active low used to trigger 
//the read of an event code and
//the 7th pin is used to signal a 
//good trial (on rising edge)
eventCode.value = 0x40;

ScriptFunction.Script:
//In Arnsten Pictobox, the 8th pin
//is active low used to trigger 
//the read of an event code and
//the 7th pin is used to signal a 
//good trial (on rising edge)
//the 7th and 8th bits in the event
//code received by spike 2 are
//attached to the 6th pin in the picto
//box meaning that a top nybble of
//3 signifies a top nybble of F to 
//spike2.
eventCode.value = 0x30 | code;

StateMachineElement.EntryScript:
//If we haven't yet used the current value numMemSacBlocks times, reshuffle it.
var currValue = targetSelector.value;
var currUses = targetCounter.getDataAsInt(currValue);
currUses++;
targetCounter.setData(currValue,currUses);
if(currUses < numMemSacBlocks.value)
{
	targetSelector.reshuffleLastValue();
}

trialNum.value++;
expInfo.setData("GoodTrials",expInfo.getDataAsInt("GoodTrials")+1);
expInfo.setData("GoodTrials(%)",Math.round(100*expInfo.getDataAsDouble("GoodTrials")/(expInfo.getDataAsDouble("Trial")+1)));
expInfo.updateValue();

StateMachineElement.Script:
//Check if we're done with this block
if(trialNum.value < (numMemSacBlocks.value*numTargets.value))
	return true;
return false;

if(blockNum.value && (blockNum.value % numBlocks.value))
	return true;
return false;
