ScriptFunction.Script:
var center = monkNashPr;
//Pick computer's choice according to Nash equilibrium
//Pick of 1 means right side.
var cSelect = Math.floor(Math.random() * 3);
compChoosesRight.value = (cSelect < compNashPr.value * 3) ? 1 : 0;

//Algorithm1
for (var i = 0; i <= histDepth.value; i++) {
    if (choice.getNumEntries() <= i)
        break;
    calProb(i);
}
//Algorithm2
for (var i = 1; i <= histDepth.value; i++) {
    if (choice.getNumEntries() <= i)
        break;
    calProb1(i);
}

//Update value field of info graphics to reflect finalized data
alg1Pr.updateValue();
alg1Bias.updateValue();
alg2Pr.updateValue();
alg2Bias.updateValue();

//Update Operator Bias Information	
if(biasYes.value)
{
	biasInfo.setData("pDev",Math.round(pDev.value*100)/100);
	biasInfo.x = compChoosesRight.value?250:550;
	biasInfo.visible = true;
	biasInfo.updateValue();
	ernData.setData("maxdevpr",maxDevR.value);
}

var BINOALPHA = binoAlpha.value;
var biasFlag = 0;
var total = choice.getNumEntries();
var ntarget = 0;
var nRisky = 0;
var biasProbR = 0;

if (depth == 0)       // p(R) for entire choice set (choice_)
{
    ntarget = total;
    for (var m = 0; m < total; m++) nRisky = nRisky + choice.getDataAsInt(m);
}
else {
    // algorithm 1
    // depth 1: target = 0 or 1
    //       2:          0 to 3
    //       3:          0 to 7
    //       4:          0 to 15

    var target = 0.0;
    for (var m = 0; m < depth; m++)
        target = target + Math.pow(2, m) * choice.getDataAsInt(total - m - 1);

    for (var i = total - 2; i >= depth - 1; i--) {
        var sample = 0.0;
        for (var m = 0; m < depth; m++)
            sample = sample + Math.pow(2.0, m) * choice.getDataAsInt(i - m);

        if (sample == target) {
            ntarget++;
            nRisky = nRisky + choice.getDataAsInt(i + 1);
        }
    }
}

// biasflag 0 : around the equilibrium
//          1 : rightward bias
//          2 : leftward bias

if (ntarget > 0) {
    var upperp = binocdf(nRisky - 1, ntarget, 1.0/3.0);
    var lowerp = binocdf(nRisky, ntarget, 1.0/3.0);
    if (1 - upperp <= BINOALPHA) biasFlag = 2-lambda.value;    // risky bias
    else if (lowerp <= BINOALPHA) biasFlag = 1+lambda.value;    // safe bias
    biasProbR = (lambda.value?nRisky:ntarget-nRisky)/ ntarget;	//Rightward bias     
}
else biasProbR = -1.0;

//Write latest data out to OperatorInfo graphics
alg1Pr.setData(depth, biasProbR);
alg1Bias.setData(depth, biasFlag);

//Update the computer choice with latest data
updateCompChoice(biasFlag, biasProbR);

var BINOALPHA = binoAlpha.value;
var biasFlag = 0;
var total = choice.getNumEntries();
var ntarget = 0;
var nRisky = 0;
var biasProbR = 0;

var crcomb = new Array(total);
for (var m = 0; m < total; m++)
    crcomb[m] = 2 * choice.getDataAsInt(m) + reward.getDataAsInt(m);

// algorithm 2
// depth 1: target = 0 or 1
//       2:          0 to 3
//       3:          0 to 7
//       4:          0 to 15

var target = 0.0;
for (var m = 0; m < depth; m++)
    target = target + Math.pow(4, m) * crcomb[total - m - 1];

for (var i = total - 2; i >= depth - 1; i--) {
    var sample = 0.0;
    for (var m = 0; m < depth; m++)
        sample = sample + Math.pow(4.0, m) * crcomb[i - m];

    if (sample == target) {
        ntarget++;
        nRisky = nRisky + choice.getDataAsInt(i + 1);
    }
}

// biasflag 0 : around the equilibrium
//          1 : rightward bias
//          2 : leftward bias

if (ntarget > 0) {
    var upperp = binocdf(nRisky - 1, ntarget, 1.0/3.0);
    var lowerp = binocdf(nRisky, ntarget, 1.0/3.0);

    if (1 - upperp <= BINOALPHA) biasFlag = 2-lambda.value; 	//Risky Bias
    else if (lowerp <= BINOALPHA) biasFlag = 1+lambda.value;	//Safe Bias
    biasProbR = (lambda.value?nRisky:ntarget-nRisky)/ ntarget; //Rightward bias
}
else biasProbR = -1.0;

//Write latest data out to OperatorInfo graphics
alg2Pr.setData(depth, biasProbR);
alg2Bias.setData(depth, biasFlag);

//Update the computer choice with latest data
updateCompChoice(biasFlag, biasProbR);

var center = monkNashPr.value;
if ((biasFlag == 1) && (biasProbR - center > pDev.value)) {
    pDev.value = biasProbR - center;
    maxDevR.value = biasProbR;
    compChoosesRight.value = 0;
    biasYes.value = true;
}
else if ((biasFlag == 2) && (center - biasProbR > pDev.value)) {
    pDev.value = center - biasProbR;
    maxDevR.value = biasProbR;
    compChoosesRight.value = 1;
    biasYes.value = true;
}

var newx = 0;
if (x + 1.0 >= n)
    newx = n;
else
    newx = x + 1.0;
return 1.0-betai(newx, n-newx+1.0, p);

var bt = 0.0;

if (x < 0.0 || x > 1.0) return 0.5;
if (x == 0.0 || x == 1.0) bt = 0.0;
else
    bt = Math.exp(gammln(a + b) - gammln(a) - gammln(b) + a * Math.log(x) + b * Math.log(1.0 - x));
if (x < (a + 1.0) / (a + b + 2.0))
    return bt * betacf(a, b, x) / a;
else
    return 1.0 - bt * betacf(b, a, 1.0 - x) / b;

var FPMIN = 1.0e-30
var EPS = 3.0e-7;
var MAXIT = 100;
var qab = a + b;
var qap = a + 1.0;
var qam = a - 1.0;
var c = 1.0;
var d = 1.0 - qab * x / qap;
var aa = 0;
var del = 0;
if (Math.abs(d) < FPMIN) d = FPMIN;
d = 1.0 / d;
var h = d;
for (var m = 1; m <= MAXIT; m++) {
    m2 = 2 * m;
    aa = m * (b - m) * x / ((qam + m2) * (a + m2));
    d = 1.0 + aa * d;
    if (Math.abs(d) < FPMIN) d = FPMIN;
    c = 1.0 + aa / c;
    if (Math.abs(c) < FPMIN) c = FPMIN;
    d = 1.0 / d;
    h *= d * c;
    aa = -(a + m) * (qab + m) * x / ((a + m2) * (qap + m2));
    d = 1.0 + aa * d;
    if (Math.abs(d) < FPMIN) d = FPMIN;
    c = 1.0 + aa / c;
    if (Math.abs(c) < FPMIN) c = FPMIN;
    d = 1.0 / d;
    del = d * c;
    h *= del;
    if (Math.abs(del - 1.0) < EPS) break;
}
return h;

StateMachineElement.EntryScript:
if ((compChoosesRight.value && rCircle.visible)
                || (!compChoosesRight.value && lCircle.visible)) {	//User Win
    feedbackRing.red = 255;
	ernData.setData("outcomecolor",0);
}
else if (rCircle.visible == rightIsSafe.value) {	//Neutral
    feedbackRing.blue = 255;
	ernData.setData("outcomecolor",1);
