	ernData.setData("outcomecolor",2);
	if(ernData.getDataAsInt("monkeyPick")>0)
		blockInfo.setData("TotalRLoss",blockInfo.getDataAsInt("TotalRLoss")+1);
	else
		blockInfo.setData("TotalLLoss",blockInfo.getDataAsInt("TotalLLoss")+1);

}
