ernData.setData("pickedTargX",feedbackRing.x);
ernData.setData("pickedTargY",feedbackRing.y);

var tokens = [token0, token1, token2, token3, token4, token5];
if (feedbackRing.red == 255) {	//Win
    tokens[tokensCollected.value].outline = false;
    tokensCollected.value++;
    numGain.value++;
    reward.setData(reward.getNumEntries(), 1);
	ernData.setData("rewcount",1);
}
else if (feedbackRing.blue != 255) {	//Lose
    if(tokensCollected.value > 0)
    {
        tokens[tokensCollected.value - 1].outline = true;
        tokensCollected.value--;
    }
    numLoss.value++;
    reward.setData(reward.getNumEntries(), 0);
    ernData.setData("rewcount",-1);
}
else
{
    numNeutral.value++;
    reward.setData(reward.getNumEntries(), 0);
	ernData.setData("rewcount",0);
}
blockInfo.setData("Gain%",Math.round(100*(numGain.value)/(trialNum.value+1)));
blockInfo.setData("Loss%",Math.round(100*(numLoss.value)/(trialNum.value+1)));
blockInfo.setData("Neutral%",Math.round(100*(numNeutral.value)/(trialNum.value+1)));
reward.updateValue();

StateMachineElement.Script:
rCircle.visible = false;
feedbackRing.x=250;
choice.setData(choice.getNumEntries(),!lambda.value);
choice.updateValue();
ernData.setData("monkeyPick",0);
return true;

lCircle.visible = false;
feedbackRing.x=550;
choice.setData(choice.getNumEntries(),lambda.value);
choice.updateValue();
ernData.setData("monkeyPick",1);
return true;

StateMachineElement.EntryScript:
fixTarget.outside = true;
MoveController.totalTime = saccadeWaitTime.value;
MoveController.maxAcquisitionTime = saccadeWaitTime.value;

StateMachineElement.Script:
blockInfo.updateValue();
if(trialNum.value < minTrialsPerBlock.value)
    return false;
if(Math.random()*100 >= switchProb.value)
    return false;
return true;

StateMachineElement.ExitScript:
fixGraphic.visible = false;
tokensCollected.value = Math.floor(Math.random() * 3) + 2;
var tokens = [token0, token1, token2, token3, token4, token5];
for (i = 0; i < tokens.length; i++) {
    tokens[i].visible = false;
}

StateMachineElement.EntryScript:
var tokens = [token0, token1, token2, token3, token4, token5];
for (var i = 0; i < tokens.length; i++) {
    tokens[i].outline = i >= tokensCollected.value;
    tokens[i].visible = true;
}


//Reset all ernData values
ernData.setData("totalTrial",ernData.getDataAsInt("totalTrial")+1);
ernData.setData("totalTrialInBlock",ernData.getDataAsInt("totalTrialInBlock")+1);
ernData.setData("goodTrialInBlock",-1);
ernData.setData("blockNumber",blockNum.value);
