StateMachineElement.EntryScript:
Square.alpha = 255;
colWidth = Grid.width/Grid.columns;
rowHeight = Grid.height/Grid.rows;
Square.x = operatorClick.x-(operatorClick.x%colWidth)+colWidth/2;
Square.y = operatorClick.y-(operatorClick.y%rowHeight)+rowHeight/2;
Square.width = SquareDimension.value;
Square.height = SquareDimension.value;
Square.subjectView = true;
Target.x = Square.x;
Target.y = Square.y;

StateMachine.EntryScript:
if (Math.random() < .5)
    lambda.value = 0;
else
    lambda.value = 1;
tokensCollected.value = Math.floor(Math.random() * 3) + 2;

StateMachineElement.EntryScript:
blockNum.value++;
blockInfo.setData("Block",blockNum.value);
lambda.value = !lambda.value;
rightIsSafe.value = lambda.value;

//Initialize Monkey and Computer Nash Probabilities
monkNashPr.value = (2 - lambda.value) / 3;
compNashPr.value = (1 + lambda.value) / 3;

//Computer picks its token
drawCompPick();

fixTarget.radius = fixationWindowRadius.value;
lTarget.radius = saccadeWindowRadius.value;
rTarget.radius = saccadeWindowRadius.value;

ScriptFunction.Script:
var center = monkNashPr;
//Pick computer's choice according to Nash equilibrium
//Pick of 1 means right side.
var cSelect = Math.floor(Math.random() * 3);
compChoosesRight.value = (cSelect < compNashPr.value * 3) ? 1 : 0;

//Algorithm1
for (var i = 0; i < histDepth.value; i++) {
    if (choice.getNumEntries() <= i)
        break;
    calProb(i);
}
//Algorithm2
for (var i = 0; i < histDepth.value; i++) {
    if (choice.getNumEntries() <= i)
        break;
    calProb1(i);
}

//Update value field of info graphics to reflect finalized data
alg1Pr.updateValue();
alg1Bias.updateValue();
alg2Pr.updateValue();
alg2Bias.updateValue();

//Update Operator Bias Information	
if(biasYes.value)
{
	monkeyBiasL.text =  "Bias: " + (1-maxDevR.value);
	monkeyBiasR.text = "Bias: " + maxDevR.value;
}

var BINOALPHA = binoAlpha.value;
var biasFlag = 0;
var total = choice.getNumEntries();
var ntarget = 0;
var nright = 0;
var biasProbR = 0;

if (depth == 0)       // p(R) for entire choice set (choice_)
{
    ntarget = total;
    for (var m = 0; m < total; m++) nright = nright + choice.getDataAsInt(m);
}
else {
    // algorithm 1
    // depth 1: target = 0 or 1
    //       2:          0 to 3
    //       3:          0 to 7
    //       4:          0 to 15

    var target = 0.0;
    for (var m = 0; m < depth; m++)
        target = target + Math.pow(2, m) * choice.getDataAsInt(total - m - 1);

    for (var i = total - 2; i >= depth - 1; i--) {
        var sample = 0.0;
        for (var m = 0; m < depth; m++)
            sample = sample + Math.pow(2.0, m) * choice.getDataAsInt(i - m);

        if (sample == target) {
            ntarget++;
            nright = nright + choice.getDataAsInt(i + 1);
        }
    }
}

// biasflag 0 : around the equilibrium
//          1 : rightward bias
//          2 : leftward bias

if (ntarget > 0) {
    // rightward bias
    var upperp = binocdf(nright - 1, ntarget, monkNashPr.value);
    // leftward bias
    var lowerp = binocdf(nright, ntarget, monkNashPr.value);

    if (1 - upperp <= BINOALPHA) biasFlag = 1;
    else if (lowerp <= BINOALPHA) biasFlag = 2;
    biasProbR = nright / ntarget;
}
else biasProbR = -1.0;

//Write latest data out to OperatorInfo graphics
alg1Pr.setData(depth, biasProbR);
alg1Bias.setData(depth, biasFlag);

//Update the computer choice with latest data
updateCompChoice(biasFlag, biasProbR);

var BINOALPHA = binoAlpha.value;
var biasFlag = 0;
var total = choice.getNumEntries();
var ntarget = 0;
var nright = 0;
var biasProbR = 0;

var crcomb = new Array(total);
for (var m = 0; m < total; m++)
    crcomb[m] = 2 * choice.getDataAsInt(m) + reward.getDataAsInt(m);

if (depth == 0)       // p(R) for entire choice set (choice_)
{
    ntarget = total;
    for (var m = 0; m < total; m++) nright = nright + choice.getDataAsInt(m);
}
else {
    // algorithm 2
    // depth 1: target = 0 or 1
    //       2:          0 to 3
    //       3:          0 to 7
    //       4:          0 to 15

    var target = 0.0;
    for (var m = 0; m < depth; m++)
        target = target + Math.pow(4, m) * crcomb[total - m - 1];

    for (var i = total - 2; i >= depth - 1; i--) {
        var sample = 0.0;
        for (var m = 0; m < depth; m++)
            sample = sample + Math.pow(4.0, m) * crcomb[i - m];

        if (sample == target) {
            ntarget++;
            nright = nright + choice.getDataAsInt(i + 1);
        }
    }
}

// biasflag 0 : around the equilibrium
//          1 : rightward bias
//          2 : leftward bias

if (ntarget > 0) {
    // rightward bias
    var upperp = binocdf(nright - 1, ntarget, monkNashPr.value);
    // leftward bias
    var lowerp = binocdf(nright, ntarget, monkNashPr.value);

    if (1 - upperp <= BINOALPHA) biasFlag = 1;
    else if (lowerp <= BINOALPHA) biasFlag = 2;
    biasProbR = nright / ntarget;
}
else biasProbR = -1.0;

//Write latest data out to OperatorInfo graphics
alg2Pr.setData(depth, biasProbR);
alg2Bias.setData(depth, biasFlag);

//Update the computer choice with latest data
updateCompChoice(biasFlag, biasProbR);

var center = monkNashPr.value;
if ((biasFlag == 1) && (biasProbR - center > pDev.value)) {
    pDev.value = biasProbR - center;
    maxDevR.value = biasProbR;
    compChoosesRight.value = 0;
    biasYes.value = true;
}
else if ((biasFlag == 2) && (center - biasProbR > pDev.value)) {
    pDev.value = center - biasProbR;
    maxDevR.value = biasProbR;
    compChoosesRight.value = 1;
    biasYes.value = true;
}

var newx = 0;
if (x + 1.0 >= n)
    newx = n;
else
    newx = x + 1.0;
return 1.0-betai(newx, n-newx+1.0, p);

var bt = 0.0;

if (x < 0.0 || x > 1.0) return 0.5;
if (x == 0.0 || x == 1.0) bt = 0.0;
else
    bt = Math.exp(gammln(a + b) - gammln(a) - gammln(b) + a * Math.log(x) + b * Math.log(1.0 - x));
if (x < (a + 1.0) / (a + b + 2.0))
    return bt * betacf(a, b, x) / a;
else
    return 1.0 - bt * betacf(b, a, 1.0 - x) / b;

var FPMIN = 1.0e-30
var EPS = 3.0e-7;
var MAXIT = 100;
var qab = a + b;
var qap = a + 1.0;
var qam = a - 1.0;
var c = 1.0;
var d = 1.0 - qab * x / qap;
var aa = 0;
var del = 0;
if (Math.abs(d) < FPMIN) d = FPMIN;
d = 1.0 / d;
var h = d;
for (var m = 1; m <= MAXIT; m++) {
    m2 = 2 * m;
    aa = m * (b - m) * x / ((qam + m2) * (a + m2));
    d = 1.0 + aa * d;
    if (Math.abs(d) < FPMIN) d = FPMIN;
    c = 1.0 + aa / c;
    if (Math.abs(c) < FPMIN) c = FPMIN;
    d = 1.0 / d;
    h *= d * c;
    aa = -(a + m) * (qab + m) * x / ((a + m2) * (qap + m2));
    d = 1.0 + aa * d;
    if (Math.abs(d) < FPMIN) d = FPMIN;
    c = 1.0 + aa / c;
    if (Math.abs(c) < FPMIN) c = FPMIN;
    d = 1.0 / d;
    del = d * c;
    h *= del;
    if (Math.abs(del - 1.0) < EPS) break;
}
return h;

StateMachineElement.EntryScript:
if ((compChoosesRight.value && rCircle.visible)
                || (!compChoosesRight.value && lCircle.visible)) {	//User Win
    feedbackRing.red = 255;
}
else if (rCircle.visible == rightIsSafe.value) {	//Neutral
    feedbackRing.blue = 255;
}
else {	//User Lose
    feedbackRing.setColor(192, 192, 192, 255);
}

feedbackRing.visible = true;

var tokens = [token0, token1, token2, token3, token4, token5];
if (feedbackRing.red == 255) {	//Win
    tokens[tokensCollected.value].outline = false;
    tokensCollected.value++;
    numGain.value++;
    reward.setData(reward.getNumEntries(), 1);
}
else if (feedbackRing.blue != 255 && tokensCollected.value > 0) {	//Lose
    tokens[tokensCollected.value - 1].outline = true;
    tokensCollected.value--;
    numLoss.value++;
    reward.setData(reward.getNumEntries(), 0);
}
else
{
    numNeutral.value++;
    reward.setData(reward.getNumEntries(), 0);
}
blockInfo.setData("Gain%",Math.round(100*(numGain.value)/(trialNum.value+1)));
blockInfo.setData("Loss%",Math.round(100*(numLoss.value)/(trialNum.value+1)));
blockInfo.setData("Neutral%",Math.round(100*(numNeutral.value)/(trialNum.value+1)));
reward.updateValue();

StateMachineElement.Script:
rCircle.visible = false;
feedbackRing.x=250;
choice.setData(choice.getNumEntries(),0);
choice.updateValue();
return true;

lCircle.visible = false;
feedbackRing.x=550;
choice.setData(choice.getNumEntries(),1);
choice.updateValue();
return true;

blockInfo.updateValue();
if(trialNum.value < minTrialsPerBlock.value)
    return false;
if(Math.random()*100 >= switchProb.value)
    return false;
return true;

StateMachineElement.ExitScript:
fixGraphic.visible = false;
tokensCollected.value = Math.floor(Math.random() * 3) + 2;
var tokens = [token0, token1, token2, token3, token4, token5];
for (i = 0; i < tokens.length; i++) {
    tokens[i].visible = false;
}

StateMachineElement.EntryScript:
var tokens = [token0, token1, token2, token3, token4, token5];
for (var i = 0; i < tokens.length; i++) {
    tokens[i].outline = i >= tokensCollected.value;
    tokens[i].visible = true;
}
