Period.Script:
//Function for getting first time a trigger occurs in the current trial
function getFirstTime(list) {
if (list.length)
return Math.round(1000 * (list[0].time.value - startTime));
return -1;
}

//Initialize variables
var expInfo = "-1,-1,-1,-1,-1";
if (NeedsTrialsEnds.length)
expInfo = NeedsTrialsEnds[0].expInfo.value;
else if (ReshuffleTrialEnds.length)
expInfo = ReshuffleTrialEnds[0].expInfo.value;

//Build up .eye output
var eyeStartTime = -1;
var eyeEndTime = -1;
if (eye.length) {
eyeStartTime = Math.round(1000 * (eye[0].data.time - startTime));
eyeEndTime = Math.round(1000 * (eye[eye.length - 1].data.time - startTime));
}
var neyes = 0;
var xThenY = "";
for (var i = 0; i < eye.length; i++) {
neyes++;
xThenY += "," + eye[i].data.value[0];   //xs
}

for (var i = 0; i < eye.length; i++) {
xThenY += "," + eye[i].data.value[1];   //ys
}
var eyeStr = expInfo + ","
+ eyeStartTime + ","
+ eyeEndTime + ","
+ neyes;
if (neyes > 0) {
eyeStr += xThenY;
}

eyeFile.writeBinary(eyeStr, "int,int,int,int,int,int,int,int,short");

//Build up .lfp output
if (lfp.length) {
var lfpStartTime = Math.round(1000 * (lfp[0].data.time - startTime));
var lfpEndTime = Math.round(1000 * (lfp[lfp.length - 1].data.time - startTime));

var lfpStr = expInfo + "," + lfpStartTime + "," + lfpEndTime + ",";

//Loop through lfp building up data strings for each channel and counting total number of lfp vals
var totChans = 0;
var lfpStrs = [];
for (var i = 0; i < lfp.length; i++) {
var chan = lfp[i].data.channel;
var lfpTime = "" + Math.round(1000 * (lfp[i].data.time));
if (typeof lfpStrs[chan] == 'undefined') {
totChans++;
lfpStrs[chan] = [];
lfpStrs[chan].text = "";
lfpStrs[chan].num = 0;
lfpStrs[chan].startTime = lfpTime;
lfpStrs[chan].time_per_samp = 0;
}
if (lfpStrs[chan].num == 1) {
lfpStrs[chan].time_per_samp = lfpTime - lfpStrs[chan].startTime;
}
lfpStrs[chan].text += "," + lfp[i].data.value;
lfpStrs[chan].num++;
}
//Create strings here.
var nLfp = 0;
lfpStr += totChans + ",";
for (var i = 0; i < lfpStrs.length; i++) {
if (typeof lfpStrs[i] == 'undefined')
continue;
//Chan,Unit,Num,All Times
lfpStr += i + "," + lfpStrs[i].startTime + "," + lfpStrs[i].time_per_samp + ",";
nLfp = lfpStrs[i].num;
}
lfpStr += nLfp;   //They should all have the same nLFP so we only print it once
lfpFile.writeBinary(lfpStr, "int"); //Write all ints to file
var lfpDatStr = "";
for (var i = 0; i < lfpStrs.length; i++) {
if (typeof lfpStrs[i] == 'undefined')
continue;
//Data
lfpDatStr += lfpStrs[i].text;
}
lfpFile.writeBinary(lfpDatStr.substring(1), "float");    //Don't use first comma
}

//Build up .swf output
if (spikes.length) {
var swfStr = "";
var totTrials = expInfo.split(",")[0];
for (var i = 0; i < spikes.length; i++) {

swfStr += totTrials + ","; //totalTrial
swfStr += spikes[i].data.channel + ","; //channel
swfStr += spikes[i].data.unit + ","; //unit
swfStr += Math.round(1000 * (spikes[i].time.value - startTime)) + ","; //spiketime
swfStr += (1000 * spikes[i].data.samplePeriod) + ","; //time per sample
swfStr += spikes[i].data.waveSize + ","; //size
swfStr += spikes[i].data.wave; //wave
swfStr += "\n";
}
swfStr = swfStr.replace(/,/g, " ");
swfFile.writeText(swfStr);
}

//Function for getting first time a trigger occurs in the current trial
function getFirstTime(list) {
if (list.length)
return Math.round(1000 * (list[0].time.value - startTime));
return -1;
}

//Initialize variables
var expInfo = "-1,-1,-1,-1,-1";
if (NeedsTrialsEnds.length)
expInfo = NeedsTrialsEnds[0].expInfo.value;
else if (ReshuffleTrialEnds.length)
expInfo = ReshuffleTrialEnds[0].expInfo.value;
var fixStarts = getFirstTime(FixationStarts);
var sampleStarts = getFirstTime(SampleStarts);
var memStarts = getFirstTime(MemoryStarts);
var testStarts = getFirstTime(TestStarts);
var leavesCenter = getFirstTime(LeavesCenter);
var entersTarget = -1;
for (var i = 0; i < EntersTarget.length; i++) {
if (EntersTarget[i].value == true) {
entersTarget = Math.round(1000 * (EntersTarget[0].time.value - startTime));
}
}
var rewStarts = getFirstTime(RewardStarts);
var trialEnds = getFirstTime(TrialEnds);
var targetId = -1, trayRadius = -1, tokenRadius = -1, memIntensity = -1, nonMemIntensity = -1, endIntensity = -1,
fixWinRad = -1, sacWinRad = -1;
var targetPos = "-1,-1";
if (FixationStarts.length) {
targetId = FixationStarts[0].targetId.value;
targetPos = FixationStarts[0].targetPos.value;
trayRadius = FixationStarts[0].trayRadius.value;
tokenRadius = FixationStarts[0].tokenRadius.value;
memIntensity = FixationStarts[0].memIntensity.value;
nonMemIntensity = FixationStarts[0].nonMemIntensity.value;
endIntensity = FixationStarts[0].endIntensity.value;
fixWinRad = FixationStarts[0].fixWinRad.value;
sacWinRad = FixationStarts[0].sacWinRad.value;
}

//Create string
var behavStr = expInfo + ","
+ fixStarts + ","
+ sampleStarts + ","
+ memStarts + ","
+ testStarts + ","
+ leavesCenter + ","
+ entersTarget + ","
+ rewStarts + ","
+ trialEnds + ","
+ targetId + ","
+ targetPos + ","
+ trayRadius + ","
+ tokenRadius + ","
+ memIntensity + ","
+ nonMemIntensity + ","
+ endIntensity + ","
+ fixWinRad + ","
+ sacWinRad + "\n";

//Put it in the file
behavFile.writeText(behavStr);

StateMachineElement.EntryScript:
square.alpha = 255;
square.x = targetLocs.getTokenX(selectedTarget.value);
square.y = targetLocs.getTokenY(selectedTarget.value);
square.width = targetSize.value;
square.height = targetSize.value;
square.subjectView = true;
target.x = square.x;
target.y = square.y;

ControlElement.Script:
if(!operatorClick.newClick)
	return false;
var x = operatorClick.x;
var y = operatorClick.y;
for(var i=0;i<targetLocs.numTokens;i++)
{
	var targetX = targetLocs.getTokenX(i);
	var targetY = targetLocs.getTokenY(i);
	var targetHalfSize = targetSize.value;
	if( (Math.abs(x - targetX) <= targetHalfSize)
		&& (Math.abs(y - targetY) <= targetHalfSize)
	)
	{
		selectedTarget.value = i;
		return true;
	}
}
return false;

StateMachineElement.EntryScript:
target.radius = fixationRadius.value;
targetLocs.numTokens = numTargets.value;
targetLocs.width = trayRadius.value*2;
targetLocs.height = trayRadius.value*2;
targetLocs.tokenWidth = targetSize.value;
targetLocs.tokenHeight = targetSize.value;

StateMachine.EntryScript:
expInfo.setData("Trial",-1);
expInfo.setData("Block",-1);
expInfo.setData("TrialInBlock",-1);
expInfo.setData("GoodTrials",0);
expInfo.setData("GoodTrials(%)",0);
expInfo.updateValue();

StateMachineElement.EntryScript:
expInfo.setData("Block",blockNum.value);
expInfo.setData("TrialInBlock",-1);
expInfo.updateValue();
//Set targetSelctor range
targetSelector.max = numTargets.value-1;

//Initialize targetCounter array with zeroes for each target
for(var i=0;i<numTargets.value;i++)
{
targetCounter.setData(i,0);
}

StateMachineElement.Script:
//If we haven't yet used the current value numMemSacBlocks times, reshuffle it.
var currValue = targetSelector.value;
var currUses = targetCounter.getDataAsInt(currValue);
currUses++;
targetCounter.setData(currValue,currUses);
if(currUses < numMemSacBlocks.value)
{
targetSelector.reshuffleLastValue();
}

trialNum.value++;
expInfo.setData("GoodTrials",expInfo.getDataAsInt("GoodTrials")+1);
expInfo.setData("GoodTrials(%)",Math.round(100*expInfo.getDataAsDouble("GoodTrials")/(expInfo.getDataAsDouble("Trial")+1)));
expInfo.updateValue();
//Check if we're done with this block
if(trialNum.value < (numMemSacBlocks.value*numTargets.value))
return true;
return false;

StateMachineElement.EntryScript:
expInfo.setData("Trial",expInfo.getDataAsInt("Trial")+1);
expInfo.setData("TrialInBlock",expInfo.getDataAsInt("TrialInBlock")+1);
expInfo.updateValue();
tokenTray.width = 2*trayRadius.value;
tokenTray.height = 2*trayRadius.value;
tokenTray.numTokens = numTargets.value;
tokenTray.setColor(0,nonMemIntensity.value,0,255);
tokenTray.tokenWidth = tokenRadius.value*2;
tokenTray.tokenHeight = tokenRadius.value*2;
fixTarget.radius = fixWindowRadius.value;
sacTarget.radius = sacWindowRadius.value;
sacTarget.x = tokenTray.getTokenX(targetSelector.value);
sacTarget.y = tokenTray.getTokenY(targetSelector.value);

StateMachineElement.Script:
if(Math.random()*100 < centRewardProb.value)
{
centRewTrial.value = true;
return true;
}
return false;

StateMachineElement.EntryScript:
var timeUsed = testTimer.value;
TestController.totalTime = 1100 - timeUsed;
TestController.maxAcquisitionTime = 1100 - (TestController.fixationTime + timeUsed);

StateMachineElement.Script:
//Trial was not completed succefully, reshuffle this target value.
targetSelector.reshuffleLastValue();
expInfo.setData("GoodTrials(%)",Math.round(100*expInfo.getDataAsDouble("GoodTrials")/(expInfo.getDataAsDouble("Trial")+1)));
expInfo.updateValue();
return true;

if(blockNum.value && (blockNum.value % numBlocks.value))
return true;
return false;
