StateMachineElement.EntryScript:
                      EventInfo.setData("Event",9);
                      EventInfo.updateValue();
                      TargetGraphic.visible = true;

                      if(  (!RightOnly.value && ((TrialType.value%10)<5 ))  || LeftOnly.value )
                      {
                      TargetGraphic.x = 275;
                      TargetWindow.x = 275;
                      }
                      else
                      {
                      TargetGraphic.x = 525;
                      TargetWindow.x = 525;
                      }

                      if(!GreenOnly.value && (RedOnly.value || (TrialType.value<=10)))
                      {
                      TargetGraphic.setColor(2.55*TargetLuminosityPercent.value,0,0);
TrialInfo.setData("GreenProb",100);
TrialInfo.setData("GreenRew",TrialReward.number);
                      }
                      else
                      {
                      TargetGraphic.setColor(0,2.55*TargetLuminosityPercent.value,0);
TrialInfo.setData("RedProb",100);
TrialInfo.setData("RedRew",TrialReward.number);
                      }

RandDouble.randomize(); 
x = (MinOverlapTime.value-MaxOverlapTime.value)/Math.log(.05);
time = MinOverlapTime.value-x*Math.log(RandDouble.value);
if(time > MaxOverlapTime.value)time = MaxOverlapTime.value;
if(time < MinOverlapTime.value)time = MinOverlapTime.value;
OverlapFixationTracker.fixationTime = time;
OverlapFixationTracker.totalTime = time+500;

