StateMachineElement.EntryScript:
Square.alpha = 255;
colWidth = Grid.width/Grid.columns;
rowHeight = Grid.height/Grid.rows;
Square.x = operatorClick.x-(operatorClick.x%colWidth)+colWidth/2;
Square.y = operatorClick.y-(operatorClick.y%rowHeight)+rowHeight/2;
Square.width = SquareDimension.value;
Square.height = SquareDimension.value;
Square.subjectView = true;

RightGraphic.visible = false;
RewardR1.visible = false;
RewardR2.visible = false;
RewardR3.visible = false;
RewardR4.visible = false;
RewardR5.visible = false;
RewardR6.visible = false;
RewardR7.visible = false;
RewardR8.visible = false;

LeftGraphic.visible = false;
RewardL1.visible = false;
RewardL2.visible = false;
RewardL3.visible = false;
RewardL4.visible = false;
RewardL5.visible = false;
RewardL6.visible = false;
RewardL7.visible = false;
RewardL8.visible = false;

StateMachineElement.Script:
if(RightGraphic.visible) FeedbackRing.x = 600;
else
FeedbackRing.x = 200; return true;

RewardList = [RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
if(RightGraphic.visible)
	RewardList = [RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
while(RewardsChecked.value<8)
{
	RewardSelector.randomize();		
	RewardsChecked.value++;
	if(RewardList[RewardSelector.value].visible)
	{
		CurrentReward.value = RewardSelector.value;
		return true;
	}
}
return false;

StateMachineElement.FrameScript:
RewardList = [RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
if(RightGraphic.visible)
	RewardList = [RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
if(RewardList[CurrentReward.value].radius > 0)
	RewardList[CurrentReward.value].radius--;
else
	RewardList[CurrentReward.value].visible = false;

StateMachineElement.EntryScript:
if(!ForceGreenLeft.value && (ForceRedLeft.value || (TrialType.value<=10)))
{
LeftGraphic.setColor(255,0,0);
RightGraphic.setColor(0,255,0);
}
else
{
LeftGraphic.setColor(0,255,0);
RightGraphic.setColor(255,0,0);
}
LeftGraphic.visible = true;
RightGraphic.visible = true;

LRewards =[RewardL1,RewardL2,RewardL3,RewardL4,RewardL5,RewardL6,RewardL7,RewardL8];
RRewards =[RewardR1,RewardR2,RewardR3,RewardR4,RewardR5,RewardR6,RewardR7,RewardR8];
GreenRewardList = LRewards;
RedRewardList = RRewards;
if(LeftGraphic.green > 0)
{
	GreenRewardList = LRewards;
	RedRewardList = RRewards;
}
for(i=0;i<GreenRewards.value;i++)
{
	RewardSelector.randomize();
	GreenRewardList[RewardSelector.value].visible = true;
}
RewardSelector.reset();
for(i=0;i<RedRewards.value;i++)
{
	RewardSelector.randomize();
	RedRewardList[RewardSelector.value].visible = true;
}
Timer.restart();

StateMachineElement.Script:
switch(TrialType.value%10)
{
case 0:
  GreenRewards.value = 8;
  RedRewards.value = 1;
  break;
case 1:
  GreenRewards.value = 4;
  RedRewards.value = 1;
  break;
case 2:
  GreenRewards.value = 2;
  RedRewards.value = 1;
  break;
case 3:
  GreenRewards.value = 1;
  RedRewards.value = 1;
  break;
case 4:
  GreenRewards.value = 4;
  RedRewards.value = 2;
  break;
case 5:
  GreenRewards.value = 1;
  RedRewards.value = 2;
  break;
case 6:
  GreenRewards.value = 4;
  RedRewards.value = 4;
  break;
case 7:
  GreenRewards.value = 2;
  RedRewards.value = 4;
  break;
case 8:
  GreenRewards.value = 1;
  RedRewards.value = 4;
  break;
case 9:
  GreenRewards.value = 1;
  RedRewards.value = 8;
  break;
}
