Period.Script:
str1="";
str2="";
onTime = -1;
timeOn = 0;
definedTimeOn = -1;
for(i=0;i<eye.length;i++)
{
if((onTime < 0) && (eye[i].data.value[1]>1000))
onTime = (eye[i].time.value-startTime)*1000;
if((timeOn == 0) && (onTime > 0) && (eye[i].data.value[1]<1000))
timeOn = ((eye[i].time.value-startTime)*1000)-onTime;
definedTimeOn = eye[i].rewardQuant.value;
}
eyeFile.writeLine(
""+definedTimeOn+","+Math.abs(timeOn-definedTimeOn)+","+Math.round(10*onTime)/10
);
