AnalysisDefinition.Script:
readme.writeLine("Name:\t" + name);
readme.writeLine("Started:\t" +startTime);
readme.writeLine("Ended:\t" +endTime);
readme.writeLine("Run Number:\t" + runNumber);
readme.writeLine("-----Notes-----");
readme.writeLine(notes);

Period.Script:
for(i=0;i<frame.length;i++)
{
framesSoFar.value++;
if(lastFrameTime.value < 0)
{
lastFrameTime.value = frame.time.value(i);
continue;
}
frameLength = frame.time.value(i)-lastFrameTime.value;
lastFrameTime.value = frame.time.value(i);
if(frameLength > .02)
{
outFile.writeLine(  ""+frameLength
                    +","+frame.time.value(i)
                    +","+frame.currElem.name(i)
                    +","+framesSoFar.value
);
}
}
