AnalysisDefinition.Script:
readme.writeLine("Name:\t" + name);
readme.writeLine("Started:\t" +startTime);
readme.writeLine("Ended:\t" +endTime);
readme.writeLine("Run Number:\t" + runNumber);
readme.writeLine("-----Notes-----");
readme.writeLine(notes);

Period.Script:
for(i=0;i<frame.length;i++)
{
framesSoFar.value++;
if(lastFrameTime.value < 0)
{
lastFrameTime.value = frame[i].time.value;
continue;
}
frameLength = frame[i].time.value-lastFrameTime.value;
lastFrameTime.value = frame[i].time.value;
if(frameLength > .02)
{
outFile.writeLine(
                  ""+framesSoFar.value
                  +","+Math.round(1000*frameLength)/1000
                  +","+frame[i].currElem.name
                  +","+Math.round(1000*frame[i].time.value)/1000
);
}
}
